import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { EmployeeService } from '../../services/employee.service';
import { Employee } from '../../models/employee.model';

@Component({
  selector: 'app-employees',
  templateUrl: './employees.component.html',
  styleUrls: ['./employees.component.css']
})
export class EmployeesComponent implements OnInit {
  employees: Employee[] = [];
  filteredEmployees: Employee[] = [];
  loading = true;
  searchTerm = '';
  filterStatus = '';

  constructor(
    private employeeService: EmployeeService,
    private router: Router
  ) {}

  ngOnInit() {
    this.loadEmployees();
  }

  loadEmployees() {
    this.employeeService.getEmployees().subscribe({
      next: (data) => {
        this.employees = data;
        this.filteredEmployees = data;
        this.loading = false;
      },
      error: () => {
        this.loading = false;
      }
    });
  }

  filterEmployees() {
    this.filteredEmployees = this.employees.filter(emp => {
      const matchesSearch = !this.searchTerm || 
        emp.firstName.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
        emp.lastName.toLowerCase().includes(this.searchTerm.toLowerCase()) ||
        emp.matricule.includes(this.searchTerm);

      const matchesStatus = !this.filterStatus || emp.status === this.filterStatus;

      return matchesSearch && matchesStatus;
    });
  }

  viewEmployee(id: number) {
    this.router.navigate(['/dashboard/hr/employees', id]);
  }

  deleteEmployee(id: number) {
    if (confirm('√ätes-vous s√ªr de vouloir supprimer cet employ√©?')) {
      this.employeeService.deleteEmployee(id).subscribe({
        next: () => {
          this.loadEmployees();
        }
      });
    }
  }
}
<div class="page-container">
  <div class="page-header">
    <div>
      <h1>Gestion des Employ√©s</h1>
      <p>{{ filteredEmployees.length }} employ√©(s)</p>
    </div>
    <button routerLink="/dashboard/hr/employees/new" class="btn btn-primary">
      + Nouvel Employ√©
    </button>
  </div>

  <div class="filters">
    <input 
      type="text" 
      [(ngModel)]="searchTerm" 
      (ngModelChange)="filterEmployees()"
      placeholder="Rechercher par nom ou matricule..."
      class="search-input"
    />

    <select [(ngModel)]="filterStatus" (ngModelChange)="filterEmployees()" class="filter-select">
      <option value="">Tous les statuts</option>
      <option value="ACTIVE">Actif</option>
      <option value="INACTIVE">Inactif</option>
      <option value="ON_LEAVE">En cong√©</option>
    </select>
  </div>

  <div class="loading" *ngIf="loading">Chargement...</div>

  <div class="table-container" *ngIf="!loading">
    <table class="data-table">
      <thead>
        <tr>
          <th>Matricule</th>
          <th>Nom Complet</th>
          <th>Email</th>
          <th>D√©partement</th>
          <th>Poste</th>
          <th>Statut</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let employee of filteredEmployees">
          <td>{{ employee.matricule }}</td>
          <td>{{ employee.firstName }} {{ employee.lastName }}</td>
          <td>{{ employee.email }}</td>
          <td>{{ employee.department }}</td>
          <td>{{ employee.position }}</td>
          <td>
            <span class="status-badge" [class]="'status-' + employee.status.toLowerCase()">
              {{ employee.status }}
            </span>
          </td>
          <td class="actions">
            <button (click)="viewEmployee(employee.id)" class="btn-icon" title="Voir">üëÅÔ∏è</button>
            <button (click)="deleteEmployee(employee.id)" class="btn-icon" title="Supprimer">üóëÔ∏è</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
