import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface AuditLog {
  id: number;
  timestamp: Date;
  userId: string;
  username: string;
  action: string;
  resource: string;
  details: string;
  ipAddress: string;
  status: 'SUCCESS' | 'FAILURE';
}

@Component({
  selector: 'app-logs-audit',
  templateUrl: './logs-audit.component.html',
  styleUrls: ['./logs-audit.component.css']
})
export class LogsAuditComponent implements OnInit {
  logs: AuditLog[] = [];
  filteredLogs: AuditLog[] = [];
  loading = true;

  // Filtres
  filterAction = '';
  filterUser = '';
  filterDateFrom = '';
  filterDateTo = '';
  filterStatus = '';

  // Pagination
  currentPage = 1;
  itemsPerPage = 20;
  totalPages = 1;

  actions = ['LOGIN', 'LOGOUT', 'CREATE', 'UPDATE', 'DELETE', 'VIEW', 'EXPORT'];

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.loadLogs();
  }

  loadLogs(): void {
    // G√©n√©rer des logs de d√©monstration
    this.logs = this.generateDemoLogs();
    this.filteredLogs = [...this.logs];
    this.totalPages = Math.ceil(this.filteredLogs.length / this.itemsPerPage);
    this.loading = false;
  }

  generateDemoLogs(): AuditLog[] {
    const users = ['sahar_admin', 'sahar_chief', 'sahar_agent', 'sahar_citizen'];
    const actions = ['LOGIN', 'LOGOUT', 'CREATE', 'UPDATE', 'DELETE', 'VIEW'];
    const resources = ['User', 'Employee', 'Budget', 'Project', 'Claim', 'Report'];
    const statuses: ('SUCCESS' | 'FAILURE')[] = ['SUCCESS', 'FAILURE'];

    const logs: AuditLog[] = [];
    const now = new Date();

    for (let i = 0; i < 100; i++) {
      const date = new Date(now.getTime() - Math.random() * 7 * 24 * 60 * 60 * 1000);
      const user = users[Math.floor(Math.random() * users.length)];
      const action = actions[Math.floor(Math.random() * actions.length)];
      const resource = resources[Math.floor(Math.random() * resources.length)];
      const status = Math.random() > 0.1 ? 'SUCCESS' : 'FAILURE';

      logs.push({
        id: i + 1,
        timestamp: date,
        userId: `user-${i}`,
        username: user,
        action: action,
        resource: resource,
        details: `${action} ${resource} - ${status}`,
        ipAddress: `192.168.1.${Math.floor(Math.random() * 255)}`,
        status: status
      });
    }

    return logs.sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());
  }

  applyFilters(): void {
    this.filteredLogs = this.logs.filter(log => {
      let match = true;

      if (this.filterAction && log.action !== this.filterAction) {
        match = false;
      }

      if (this.filterUser && !log.username.toLowerCase().includes(this.filterUser.toLowerCase())) {
        match = false;
      }

      if (this.filterStatus && log.status !== this.filterStatus) {
        match = false;
      }

      if (this.filterDateFrom) {
        const fromDate = new Date(this.filterDateFrom);
        if (log.timestamp < fromDate) {
          match = false;
        }
      }

      if (this.filterDateTo) {
        const toDate = new Date(this.filterDateTo);
        toDate.setHours(23, 59, 59);
        if (log.timestamp > toDate) {
          match = false;
        }
      }

      return match;
    });

    this.currentPage = 1;
    this.totalPages = Math.ceil(this.filteredLogs.length / this.itemsPerPage);
  }

  clearFilters(): void {
    this.filterAction = '';
    this.filterUser = '';
    this.filterDateFrom = '';
    this.filterDateTo = '';
    this.filterStatus = '';
    this.applyFilters();
  }

  getPaginatedLogs(): AuditLog[] {
    const start = (this.currentPage - 1) * this.itemsPerPage;
    const end = start + this.itemsPerPage;
    return this.filteredLogs.slice(start, end);
  }

  nextPage(): void {
    if (this.currentPage < this.totalPages) {
      this.currentPage++;
    }
  }

  previousPage(): void {
    if (this.currentPage > 1) {
      this.currentPage--;
    }
  }

  exportLogs(): void {
    const csv = this.convertToCSV(this.filteredLogs);
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `audit-logs-${new Date().toISOString()}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  }

  convertToCSV(logs: AuditLog[]): string {
    const headers = ['ID', 'Timestamp', 'User', 'Action', 'Resource', 'Status', 'IP Address', 'Details'];
    const rows = logs.map(log => [
      log.id,
      log.timestamp.toISOString(),
      log.username,
      log.action,
      log.resource,
      log.status,
      log.ipAddress,
      log.details
    ]);

    return [headers, ...rows].map(row => row.join(',')).join('\n');
  }

  getSuccessCount(): number {
    return this.filteredLogs.filter(log => log.status === 'SUCCESS').length;
  }

  getFailureCount(): number {
    return this.filteredLogs.filter(log => log.status === 'FAILURE').length;
  }

  getUniqueUsers(): number {
    const uniqueUsers = new Set(this.filteredLogs.map(log => log.username));
    return uniqueUsers.size;
  }
}
<div class="page-container">
  <div class="page-header">
    <div>
      <h1>üìã Logs & Audit</h1>
      <p>Historique des actions et audit trail</p>
    </div>
    <button (click)="exportLogs()" class="btn btn-primary">
      üì• Exporter les Logs
    </button>
  </div>

  <!-- Filtres -->
  <div class="filters-card">
    <h3>üîç Filtres</h3>
    <div class="filters-grid">
      <div class="filter-group">
        <label>Action:</label>
        <select [(ngModel)]="filterAction" (ngModelChange)="applyFilters()" class="form-control">
          <option value="">Toutes les actions</option>
          <option *ngFor="let action of actions" [value]="action">{{ action }}</option>
        </select>
      </div>

      <div class="filter-group">
        <label>Utilisateur:</label>
        <input 
          type="text" 
          [(ngModel)]="filterUser" 
          (ngModelChange)="applyFilters()"
          placeholder="Nom d'utilisateur"
          class="form-control"
        />
      </div>

      <div class="filter-group">
        <label>Date d√©but:</label>
        <input 
          type="date" 
          [(ngModel)]="filterDateFrom" 
          (ngModelChange)="applyFilters()"
          class="form-control"
        />
      </div>

      <div class="filter-group">
        <label>Date fin:</label>
        <input 
          type="date" 
          [(ngModel)]="filterDateTo" 
          (ngModelChange)="applyFilters()"
          class="form-control"
        />
      </div>

      <div class="filter-group">
        <label>Statut:</label>
        <select [(ngModel)]="filterStatus" (ngModelChange)="applyFilters()" class="form-control">
          <option value="">Tous les statuts</option>
          <option value="SUCCESS">Succ√®s</option>
          <option value="FAILURE">√âchec</option>
        </select>
      </div>

      <div class="filter-group">
        <button (click)="clearFilters()" class="btn btn-secondary">
          üîÑ R√©initialiser
        </button>
      </div>
    </div>
  </div>

  <!-- Statistiques -->
  <div class="stats-grid">
    <div class="stat-card">
      <div class="stat-icon">üìä</div>
      <div class="stat-content">
        <div class="stat-value">{{ filteredLogs.length }}</div>
        <div class="stat-label">Total Logs</div>
      </div>
    </div>

    <div class="stat-card success">
      <div class="stat-icon">‚úÖ</div>
      <div class="stat-content">
        <div class="stat-value">{{ getSuccessCount() }}</div>
        <div class="stat-label">Succ√®s</div>
      </div>
    </div>

    <div class="stat-card failure">
      <div class="stat-icon">‚ùå</div>
      <div class="stat-content">
        <div class="stat-value">{{ getFailureCount() }}</div>
        <div class="stat-label">√âchecs</div>
      </div>
    </div>

    <div class="stat-card">
      <div class="stat-icon">üë•</div>
      <div class="stat-content">
        <div class="stat-value">{{ getUniqueUsers() }}</div>
        <div class="stat-label">Utilisateurs Actifs</div>
      </div>
    </div>
  </div>

  <!-- Table des logs -->
  <div class="logs-table-container">
    <div class="loading" *ngIf="loading">Chargement des logs...</div>

    <table class="logs-table" *ngIf="!loading">
      <thead>
        <tr>
          <th>ID</th>
          <th>Date & Heure</th>
          <th>Utilisateur</th>
          <th>Action</th>
          <th>Ressource</th>
          <th>Statut</th>
          <th>IP</th>
          <th>D√©tails</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let log of getPaginatedLogs()">
          <td>{{ log.id }}</td>
          <td>{{ log.timestamp | date:'dd/MM/yyyy HH:mm:ss' }}</td>
          <td>
            <span class="user-badge">{{ log.username }}</span>
          </td>
          <td>
            <span class="action-badge" [class]="'action-' + log.action.toLowerCase()">
              {{ log.action }}
            </span>
          </td>
          <td>{{ log.resource }}</td>
          <td>
            <span class="status-badge" [class]="'status-' + log.status.toLowerCase()">
              {{ log.status === 'SUCCESS' ? '‚úÖ Succ√®s' : '‚ùå √âchec' }}
            </span>
          </td>
          <td>{{ log.ipAddress }}</td>
          <td class="details-cell">{{ log.details }}</td>
        </tr>
      </tbody>
    </table>

    <!-- Pagination -->
    <div class="pagination" *ngIf="!loading && filteredLogs.length > 0">
      <button 
        (click)="previousPage()" 
        [disabled]="currentPage === 1"
        class="btn btn-sm"
      >
        ‚Üê Pr√©c√©dent
      </button>

      <span class="page-info">
        Page {{ currentPage }} sur {{ totalPages }} 
        ({{ filteredLogs.length }} logs)
      </span>

      <button 
        (click)="nextPage()" 
        [disabled]="currentPage === totalPages"
        class="btn btn-sm"
      >
        Suivant ‚Üí
      </button>
    </div>

    <div class="no-data" *ngIf="!loading && filteredLogs.length === 0">
      <p>Aucun log trouv√© avec les filtres s√©lectionn√©s</p>
    </div>
  </div>
</div>
