import { Component, OnInit } from '@angular/core';
import { BudgetService } from '../../services/budget.service';
import { Project } from '../../models/budget.model';

@Component({
  selector: 'app-projects',
  templateUrl: './projects.component.html',
  styleUrls: ['./projects.component.css']
})
export class ProjectsComponent implements OnInit {
  projects: Project[] = [];
  filteredProjects: Project[] = [];
  loading = true;
  filterStatus = '';

  constructor(private budgetService: BudgetService) {}

  ngOnInit() {
    this.loadProjects();
  }

  loadProjects() {
    this.budgetService.getProjects().subscribe({
      next: (data) => {
        this.projects = data;
        this.filteredProjects = data;
        this.loading = false;
      },
      error: () => {
        this.loading = false;
      }
    });
  }

  filterProjects() {
    if (this.filterStatus) {
      this.filteredProjects = this.projects.filter(p => p.status === this.filterStatus);
    } else {
      this.filteredProjects = this.projects;
    }
  }

  deleteProject(id: number) {
    if (confirm('√ätes-vous s√ªr de vouloir supprimer ce projet?')) {
      this.budgetService.deleteProject(id).subscribe({
        next: () => {
          this.loadProjects();
        }
      });
    }
  }
}
<div class="page-container">
  <div class="page-header">
    <div>
      <h1>Gestion des Projets</h1>
      <p>{{ filteredProjects.length }} projet(s)</p>
    </div>
    <button routerLink="/dashboard/budget/projects/new" class="btn btn-primary">
      + Nouveau Projet
    </button>
  </div>

  <div class="filters">
    <select [(ngModel)]="filterStatus" (ngModelChange)="filterProjects()" class="filter-select">
      <option value="">Tous les statuts</option>
      <option value="PLANNING">Planification</option>
      <option value="IN_PROGRESS">En cours</option>
      <option value="ON_HOLD">En attente</option>
      <option value="COMPLETED">Termin√©</option>
      <option value="CANCELLED">Annul√©</option>
    </select>
  </div>

  <div class="loading" *ngIf="loading">Chargement...</div>

  <div class="table-container" *ngIf="!loading">
    <table class="data-table">
      <thead>
        <tr>
          <th>Nom</th>
          <th>D√©partement</th>
          <th>Budget</th>
          <th>D√©pens√©</th>
          <th>Progression</th>
          <th>Statut</th>
          <th>Manager</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let project of filteredProjects">
          <td><strong>{{ project.name }}</strong></td>
          <td>{{ project.department }}</td>
          <td>{{ project.budget | number }}‚Ç¨</td>
          <td>{{ project.spent | number }}‚Ç¨</td>
          <td>
            <div class="progress-container">
              <div class="progress-bar-small">
                <div class="progress-fill-small" [style.width.%]="project.progress"></div>
              </div>
              <span>{{ project.progress }}%</span>
            </div>
          </td>
          <td>
            <span class="status-badge" [class]="'status-' + project.status.toLowerCase().replace('_', '-')">
              {{ project.status }}
            </span>
          </td>
          <td>{{ project.manager }}</td>
          <td class="actions">
            <button [routerLink]="['/dashboard/budget/projects', project.id]" class="btn-icon" title="Voir">üëÅÔ∏è</button>
            <button (click)="deleteProject(project.id)" class="btn-icon" title="Supprimer">üóëÔ∏è</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
