import { Component } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../services/auth.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html'
})
export class RegisterComponent {
  loading = false;
  message = '';
  messageType: 'success' | 'error' = 'error';
  form;

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router
  ) {
    this.form = this.fb.group({
      username: ['', [Validators.required, Validators.minLength(3)]],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      confirmPassword: ['', Validators.required]
    });
  }

  submit() {
    if (this.form.invalid) {
      this.markFormGroupTouched(this.form);
      return;
    }

    const password = this.form.get('password')?.value;
    const confirmPassword = this.form.get('confirmPassword')?.value;

    if (password !== confirmPassword) {
      this.message = 'Les mots de passe ne correspondent pas';
      this.messageType = 'error';
      return;
    }

    this.loading = true;
    this.message = '';

    const request = {
      username: this.form.value.username,
      email: this.form.value.email,
      password: this.form.value.password,
      role: 'CITIZEN'
    };

    this.authService.register(request).subscribe({
      next: () => {
        this.message = 'Inscription réussie ! Redirection vers la connexion...';
        this.messageType = 'success';
        this.loading = false;
        setTimeout(() => {
          this.router.navigate(['/login']);
        }, 2000);
      },
      error: (err) => {
        this.message = err.error?.message || 'Échec de l\'inscription. Veuillez réessayer.';
        this.messageType = 'error';
        this.loading = false;
      }
    });
  }

  private markFormGroupTouched(formGroup: any) {
    Object.keys(formGroup.controls).forEach(key => {
      const control = formGroup.get(key);
      control?.markAsTouched();
    });
  }
}
<div class="auth-container">
  <div class="auth-card">
    <div class="auth-header">
      <h1>ERP Municipalité de Tunis</h1>
      <h2>Inscription</h2>
    </div>

    <form [formGroup]="form" (ngSubmit)="submit()">
      <div class="form-group">
        <label for="username">Nom d'utilisateur</label>
        <input 
          id="username"
          type="text"
          formControlName="username" 
          class="form-control"
          placeholder="Choisissez un nom d'utilisateur"
        />
        <div class="error" *ngIf="form.get('username')?.invalid && form.get('username')?.touched">
          <span *ngIf="form.get('username')?.errors?.['required']">Le nom d'utilisateur est requis</span>
          <span *ngIf="form.get('username')?.errors?.['minlength']">Le nom d'utilisateur doit contenir au moins 3 caractères</span>
        </div>
      </div>

      <div class="form-group">
        <label for="email">Email</label>
        <input 
          id="email"
          type="email"
          formControlName="email" 
          class="form-control"
          placeholder="votre.email@example.com"
        />
        <div class="error" *ngIf="form.get('email')?.invalid && form.get('email')?.touched">
          <span *ngIf="form.get('email')?.errors?.['required']">L'email est requis</span>
          <span *ngIf="form.get('email')?.errors?.['email']">L'email n'est pas valide</span>
        </div>
      </div>

      <div class="form-group">
        <label for="password">Mot de passe</label>
        <input 
          id="password"
          type="password"
          formControlName="password" 
          class="form-control"
          placeholder="Choisissez un mot de passe"
        />
        <div class="error" *ngIf="form.get('password')?.invalid && form.get('password')?.touched">
          <span *ngIf="form.get('password')?.errors?.['required']">Le mot de passe est requis</span>
          <span *ngIf="form.get('password')?.errors?.['minlength']">Le mot de passe doit contenir au moins 6 caractères</span>
        </div>
      </div>

      <div class="form-group">
        <label for="confirmPassword">Confirmer le mot de passe</label>
        <input 
          id="confirmPassword"
          type="password"
          formControlName="confirmPassword" 
          class="form-control"
          placeholder="Confirmez votre mot de passe"
        />
        <div class="error" *ngIf="form.get('confirmPassword')?.invalid && form.get('confirmPassword')?.touched">
          Le mot de passe de confirmation est requis
        </div>
      </div>

      <button type="submit" class="btn btn-primary" [disabled]="loading">
        <span *ngIf="!loading">S'inscrire</span>
        <span *ngIf="loading">Inscription en cours...</span>
      </button>

      <div class="message success" *ngIf="message && messageType === 'success'">{{ message }}</div>
      <div class="message error" *ngIf="message && messageType === 'error'">{{ message }}</div>
    </form>

    <div class="auth-footer">
      <a routerLink="/login">Vous avez déjà un compte? Se connecter</a>
    </div>
  </div>
</div>
