import { Component, OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../services/auth.service';

@Component({
  selector: 'app-otp',
  templateUrl: './otp.component.html'
})
export class OtpComponent implements OnInit {
  loading = false;
  message = '';
  form;
  sessionId = '';
  resetEmail = '';
  isPasswordReset = false;

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router
  ) {
    this.form = this.fb.group({
      otpCode: ['', [Validators.required, Validators.minLength(6), Validators.maxLength(6)]],
      newPassword: ['', [Validators.required, Validators.minLength(6)]],
      confirmPassword: ['', [Validators.required]]
    });
  }

  ngOnInit() {
    this.resetEmail = localStorage.getItem('resetEmail') || '';
    this.sessionId = localStorage.getItem('sessionId') || '';

    if (this.resetEmail) {
      this.isPasswordReset = true;
    } else if (!this.sessionId) {
      this.router.navigate(['/login']);
    }
  }

  verify() {
    if (this.form.invalid) return;

    if (this.isPasswordReset) {
      // Password reset flow
      if (this.form.value.newPassword !== this.form.value.confirmPassword) {
        this.message = 'Les mots de passe ne correspondent pas';
        return;
      }

      this.loading = true;
      this.message = '';

      const request = {
        email: this.resetEmail,
        code: this.form.value.otpCode,
        newPassword: this.form.value.newPassword
      };

      this.authService.verifyOtpAndResetPassword(request).subscribe({
        next: () => {
          localStorage.removeItem('resetEmail');
          this.message = 'Mot de passe réinitialisé avec succès! Redirection...';
          setTimeout(() => {
            this.router.navigate(['/dashboard']);
          }, 2000);
        },
        error: (err) => {
          this.message = err.error?.error || err.error?.message || 'Code OTP invalide ou expiré';
          this.loading = false;
        }
      });
    } else {
      // Normal OTP verification
      this.loading = true;
      this.message = '';

      const request = {
        sessionId: this.sessionId,
        otpCode: this.form.value.otpCode
      };

      this.authService.verifyOtp(request).subscribe({
        next: () => {
          localStorage.removeItem('sessionId');
          this.router.navigate(['/dashboard']);
        },
        error: (err) => {
          this.message = err.error?.error || err.error?.message || 'Code OTP invalide';
          this.loading = false;
        }
      });
    }
  }

  resendOtp() {
    if (this.isPasswordReset && this.resetEmail) {
      this.authService.sendResetOtp({ email: this.resetEmail }).subscribe({
        next: () => {
          this.message = 'Code OTP renvoyé à votre email';
        },
        error: () => {
          this.message = 'Erreur lors de l\'envoi du code OTP';
        }
      });
    } else {
      this.message = 'Impossible de renvoyer le code OTP';
    }
  }
}
<div class="auth-container">
  <div class="auth-card">
    <div class="auth-header">
      <h1>ERP Municipalité de Tunis</h1>
      <h2 *ngIf="!isPasswordReset">Vérification OTP</h2>
      <h2 *ngIf="isPasswordReset">Réinitialiser le mot de passe</h2>
      <p>Entrez le code OTP envoyé à votre email</p>
      <p *ngIf="isPasswordReset && resetEmail" class="email-info">Email: {{ resetEmail }}</p>
    </div>

    <form [formGroup]="form" (ngSubmit)="verify()">
      <div class="form-group">
        <label for="otpCode">Code OTP (6 chiffres)</label>
        <input 
          id="otpCode"
          type="text"
          formControlName="otpCode" 
          class="form-control"
          placeholder="000000"
          maxlength="6"
        />
        <div class="error" *ngIf="form.get('otpCode')?.invalid && form.get('otpCode')?.touched">
          Le code OTP doit contenir 6 chiffres
        </div>
      </div>

      <div *ngIf="isPasswordReset">
        <div class="form-group">
          <label for="newPassword">Nouveau mot de passe</label>
          <input 
            id="newPassword"
            type="password"
            formControlName="newPassword" 
            class="form-control"
            placeholder="••••••••"
          />
          <div class="error" *ngIf="form.get('newPassword')?.invalid && form.get('newPassword')?.touched">
            Le mot de passe doit contenir au moins 6 caractères
          </div>
        </div>

        <div class="form-group">
          <label for="confirmPassword">Confirmer le mot de passe</label>
          <input 
            id="confirmPassword"
            type="password"
            formControlName="confirmPassword" 
            class="form-control"
            placeholder="••••••••"
          />
        </div>
      </div>

      <button type="submit" class="btn btn-primary" [disabled]="loading || form.invalid">
        <span *ngIf="!loading && !isPasswordReset">Vérifier</span>
        <span *ngIf="!loading && isPasswordReset">Réinitialiser le mot de passe</span>
        <span *ngIf="loading">Traitement...</span>
      </button>

      <div class="message" [class.success]="message.includes('succès')" [class.error]="!message.includes('succès')" *ngIf="message">{{ message }}</div>
    </form>

    <div class="auth-footer">
      <a (click)="resendOtp()" style="cursor: pointer;">Renvoyer le code OTP</a>
      <br>
      <a routerLink="/login">Retour à la connexion</a>
    </div>
  </div>
</div>
