import { Component } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../services/auth.service';

@Component({
  selector: 'app-reset',
  templateUrl: './reset.component.html'
})
export class ResetComponent {
  loading = false;
  message = '';
  success = false;
  form;

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router
  ) {
    this.form = this.fb.group({
      email: ['', [Validators.required, Validators.email]]
    });
  }

  submit() {
    if (this.form.invalid) return;
    this.loading = true;
    this.message = '';
    this.success = false;

    this.authService.sendResetOtp({ email: this.form.value.email }).subscribe({
      next: () => {
        this.success = true;
        this.message = 'Un code OTP a été envoyé à votre email';
        this.loading = false;
        // Store email in localStorage for OTP verification
        localStorage.setItem('resetEmail', this.form.value.email);
        setTimeout(() => {
          this.router.navigate(['/otp']);
        }, 2000);
      },
      error: (err) => {
        this.message = err.error?.error || err.error?.message || 'Erreur lors de la réinitialisation';
        this.loading = false;
      }
    });
  }
}
<div class="auth-container">
  <div class="auth-card">
    <div class="auth-header">
      <h1>ERP Municipalité de Tunis</h1>
      <h2>Réinitialiser le mot de passe</h2>
      <p>Entrez votre email pour recevoir un lien de réinitialisation</p>
    </div>

    <form [formGroup]="form" (ngSubmit)="submit()">
      <div class="form-group">
        <label for="email">Adresse email</label>
        <input 
          id="email"
          type="email"
          formControlName="email" 
          class="form-control"
          placeholder="votre.email@exemple.com"
        />
        <div class="error" *ngIf="form.get('email')?.invalid && form.get('email')?.touched">
          Veuillez entrer une adresse email valide
        </div>
      </div>

      <button type="submit" class="btn btn-primary" [disabled]="loading || form.invalid">
        <span *ngIf="!loading">Envoyer le lien</span>
        <span *ngIf="loading">Envoi en cours...</span>
      </button>

      <div class="message" [class.success]="success" [class.error]="!success" *ngIf="message">
        {{ message }}
      </div>
    </form>

    <div class="auth-footer">
      <a routerLink="/login">Retour à la connexion</a>
    </div>
  </div>
</div>
