import { Component } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html'
})
export class LoginComponent {
  loading = false;
  message = '';
  form;

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router
  ) {
    this.form = this.fb.group({
      username: ['', Validators.required],
      password: ['', Validators.required]
    });
  }

  submit() {
    if (this.form.invalid) return;
    this.loading = true;
    this.message = '';

    this.authService.login(this.form.value).subscribe({
      next: (res) => {
        if (res.requiresOtp) {
          localStorage.setItem('sessionId', res.sessionId || '');
          this.router.navigate(['/otp']);
        } else if (res.accessToken) {
          this.router.navigate(['/dashboard']);
        }
        this.loading = false;
      },
      error: (err) => {
        this.message = err.error?.message || 'Échec de connexion';
        this.loading = false;
      }
    });
  }
}
<div class="auth-container">
  <div class="auth-card">
    <div class="auth-header">
      <h1>ERP Municipalité de Tunis</h1>
      <h2>Connexion</h2>
    </div>

    <form [formGroup]="form" (ngSubmit)="submit()">
      <div class="form-group">
        <label for="username">Nom d'utilisateur</label>
        <input 
          id="username"
          type="text"
          formControlName="username" 
          class="form-control"
          placeholder="Entrez votre nom d'utilisateur"
        />
        <div class="error" *ngIf="form.get('username')?.invalid && form.get('username')?.touched">
          Le nom d'utilisateur est requis
        </div>
      </div>

      <div class="form-group">
        <label for="password">Mot de passe</label>
        <input 
          id="password"
          type="password"
          formControlName="password" 
          class="form-control"
          placeholder="Entrez votre mot de passe"
        />
        <div class="error" *ngIf="form.get('password')?.invalid && form.get('password')?.touched">
          Le mot de passe est requis
        </div>
      </div>

      <button type="submit" class="btn btn-primary" [disabled]="loading">
        <span *ngIf="!loading">Se connecter</span>
        <span *ngIf="loading">Connexion en cours...</span>
      </button>

      <div class="message error" *ngIf="message">{{ message }}</div>
    </form>

    <div class="auth-footer">
      <a routerLink="/reset">Mot de passe oublié?</a>
      <span class="separator">•</span>
      <a routerLink="/register">Créer un compte</a>
    </div>
  </div>
</div>
