import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../services/auth.service';
import { ReportService } from '../services/report.service';
import { DashboardStats } from '../models/report.model';
import { User } from '../models/user.model';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  currentUser: User | null = null;
  stats: DashboardStats | null = null;
  loading = true;

  constructor(
    private authService: AuthService,
    private reportService: ReportService,
    private router: Router
  ) {}

  ngOnInit() {
    this.currentUser = this.authService.getCurrentUser();
    this.loadDashboardStats();
  }

  loadDashboardStats() {
    this.reportService.getDashboardStats().subscribe({
      next: (data) => {
        this.stats = data;
        this.loading = false;
      },
      error: () => {
        this.loading = false;
      }
    });
  }

  logout() {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
<div class="dashboard-container">
  <nav class="sidebar">
    <div class="sidebar-header">
      <h2>ERP Tunis</h2>
      <p *ngIf="currentUser">{{ currentUser.fullName }}</p>
    </div>

    <ul class="nav-menu">
      <li routerLinkActive="active">
        <a routerLink="/dashboard/home">
          <span class="icon">üìä</span>
          <span>Tableau de bord</span>
        </a>
      </li>

      <!-- Menu pour CITIZEN -->
      <li routerLinkActive="active" *ngIf="isCitizen()">
        <a routerLink="/dashboard/claims">
          <span class="icon">üìù</span>
          <span>Mes R√©clamations</span>
        </a>
      </li>
      <li routerLinkActive="active" *ngIf="isCitizen()">
        <a routerLink="/dashboard/home">
          <span class="icon">üîî</span>
          <span>Notifications</span>
        </a>
      </li>

      <!-- Menu pour AGENT -->
      <li routerLinkActive="active" *ngIf="isAgent() || isChief() || isAdmin()">
        <a routerLink="/dashboard/claims">
          <span class="icon">üìã</span>
          <span>R√©clamations</span>
        </a>
      </li>
      <li routerLinkActive="active" *ngIf="isAgent()">
        <a routerLink="/dashboard/home">
          <span class="icon">‚úÖ</span>
          <span>Mes T√¢ches</span>
        </a>
      </li>

      <!-- Menu pour CHIEF -->
      <li routerLinkActive="active" *ngIf="isChief() || isAdmin()">
        <a routerLink="/dashboard/hr">
          <span class="icon">üë•</span>
