      </li>
      <li routerLinkActive="active" *ngIf="isChief() || isAdmin()">
        <a routerLink="/dashboard/reports">
          <span class="icon">üìà</span>
          <span>Rapports</span>
        </a>
      </li>

      <!-- Menu pour ADMIN -->
      <li routerLinkActive="active" *ngIf="isAdmin()">
        <a routerLink="/dashboard/hr">
          <span class="icon">üë•</span>
          <span>Gestion Utilisateurs</span>
        </a>
      </li>
      <li routerLinkActive="active" *ngIf="isAdmin()">
        <a routerLink="/dashboard/budget">
          <span class="icon">üí∞</span>
          <span>Budget & Projets</span>
        </a>
      </li>
      <li routerLinkActive="active" *ngIf="isAdmin()">
        <a routerLink="/dashboard/home">
          <span class="icon">‚öôÔ∏è</span>
          <span>Configuration</span>
        </a>
      </li>
    </ul>

    <div class="sidebar-footer">
      <button (click)="logout()" class="btn-logout">
        <span class="icon">üö™</span>
        <span>D√©connexion</span>
      </button>
    </div>
  </nav>

  <main class="main-content">
    <router-outlet></router-outlet>
  </main>
</div>
