      <div class="form-group">
        <label for="year">Ann√©e Fiscale *</label>
        <select id="year" formControlName="year" class="form-control">
          <option *ngFor="let year of fiscalYears" [value]="year.value">
            {{ year.label }}
          </option>
        </select>
      </div>
