    <div class="form-section">
      <h2>Montants</h2>

      <div class="form-row">
        <div class="form-group">
          <label for="totalAllocated">Budget Allou√© (‚Ç¨) *</label>
          <input
            id="totalAllocated"
            type="number"
            formControlName="totalAllocated"
            class="form-control"
            placeholder="1000000"
            min="1000"
          />
          <div class="form-hint">Montant total allou√© pour ce budget</div>
          <div class="error" *ngIf="budgetForm.get('totalAllocated')?.invalid && budgetForm.get('totalAllocated')?.touched">
            Le montant doit √™tre sup√©rieur √† 1000‚Ç¨
          </div>
        </div>

        <div class="form-group">
          <label for="totalSpent">Montant D√©pens√© (‚Ç¨)</label>
