import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ClaimService } from '../../services/claim.service';
import { AuthService } from '../../services/auth.service';
import { Claim, ClaimComment } from '../../models/claim.model';
import { User } from '../../models/user.model';

@Component({
  selector: 'app-claim-detail',
  templateUrl: './claim-detail.component.html',
  styleUrls: ['./claim-detail.component.css']
})
export class ClaimDetailComponent implements OnInit {
  claim: Claim | null = null;
  comments: ClaimComment[] = [];
  currentUser: User | null = null;
  loading = true;

  newComment = '';
  addingComment = false;
  updatingStatus = false;

  statusOptions = [
    { value: 'NEW', label: 'Nouvelle', color: '#3b82f6' },
    { value: 'IN_PROGRESS', label: 'En cours', color: '#f59e0b' },
    { value: 'RESOLVED', label: 'R√©solue', color: '#10b981' },
    { value: 'CLOSED', label: 'Ferm√©e', color: '#6b7280' },
    { value: 'REJECTED', label: 'Rejet√©e', color: '#ef4444' }
  ];

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private claimService: ClaimService,
    private authService: AuthService
  ) {}

  ngOnInit(): void {
    this.currentUser = this.authService.getCurrentUser();
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.loadClaim(id);
      // loadComments sera appel√© apr√®s loadClaim
    } else {
      this.loading = false;
      this.router.navigate(['/dashboard/claims']);
    }
  }

  loadClaim(id: string): void {
    this.claimService.getClaim(id).subscribe({
      next: (data) => {
        this.claim = data;
        this.loading = false;
        // Charger les commentaires apr√®s avoir charg√© la r√©clamation
        this.loadComments(id);
      },
      error: (err) => {
        console.error('Error loading claim:', err);
        this.loading = false;
        // Rediriger vers la liste si r√©clamation non trouv√©e
        setTimeout(() => {
          this.router.navigate(['/dashboard/claims']);
        }, 2000);
      }
    });
  }

  loadComments(id: string): void {
    // Charger les commentaires depuis l'API (si endpoint existe)
    this.claimService.getComments(id).subscribe({
      next: (data) => {
        this.comments = data;
        // Ajouter la r√©ponse de la r√©clamation comme commentaire si elle existe
        this.addResponseAsComment();
      },
      error: (err) => {
        console.error('Error loading comments:', err);
        // Si l'endpoint n'existe pas, afficher quand m√™me la r√©ponse
        this.addResponseAsComment();
      }
    });
  }

  private addResponseAsComment(): void {
    // Si la r√©clamation a une r√©ponse, l'afficher comme commentaire
    if (this.claim?.response && this.claim.response.trim()) {
      const responseComment: ClaimComment = {
        id: 1,
        claimId: this.claim.id,
        author: 'Agent/Admin',
        comment: this.claim.response,
        createdAt: this.claim.updatedAt || this.claim.createdAt
      };

      // Ajouter uniquement si pas d√©j√† pr√©sent
      const exists = this.comments.some(c => c.comment === this.claim!.response);
      if (!exists) {
        this.comments.push(responseComment);
      }
    }
  }

  // V√©rification des r√¥les
  isCitizen(): boolean {
    return this.currentUser?.role?.toUpperCase() === 'CITIZEN';
  }

  isAgent(): boolean {
    return this.currentUser?.role?.toUpperCase() === 'AGENT';
  }

  isChief(): boolean {
    return this.currentUser?.role?.toUpperCase() === 'CHIEF';
  }

  isAdmin(): boolean {
    return this.currentUser?.role?.toUpperCase() === 'ADMIN';
  }

  // Permissions
  canAcceptReject(): boolean {
    return this.isChief() || this.isAdmin();
  }

  canRespond(): boolean {
    return this.isAgent() || this.isChief() || this.isAdmin();
  }

  canChangeStatus(): boolean {
    return this.isAgent() || this.isChief() || this.isAdmin();
  }

  // Actions
  acceptClaim(): void {
    if (!this.claim || !this.canAcceptReject()) return;

    this.updatingStatus = true;
    this.claimService.updateClaimStatus(this.claim.id, 'IN_PROGRESS').subscribe({
      next: () => {
        if (this.claim) {
          this.claim.status = 'IN_PROGRESS';
          this.addSystemComment('R√©clamation accept√©e et prise en charge');
        }
        this.updatingStatus = false;
      },
      error: (err) => {
        console.error('Error accepting claim:', err);
        this.updatingStatus = false;
      }
    });
  }

  rejectClaim(): void {
    if (!this.claim || !this.canAcceptReject()) return;

    const reason = prompt('Raison du refus:');
    if (!reason) return;

    this.updatingStatus = true;
    this.claimService.updateClaimStatus(this.claim.id, 'REJECTED', reason).subscribe({
      next: () => {
        if (this.claim) {
          this.claim.status = 'REJECTED';
          this.claim.resolution = reason;
          this.addSystemComment(`R√©clamation rejet√©e. Raison: ${reason}`);
        }
        this.updatingStatus = false;
      },
      error: (err) => {
        console.error('Error rejecting claim:', err);
        this.updatingStatus = false;
      }
    });
  }

  changeStatus(newStatus: string): void {
    if (!this.claim || !this.canChangeStatus()) return;

    this.updatingStatus = true;
    this.claimService.updateClaimStatus(this.claim.id, newStatus).subscribe({
      next: () => {
        if (this.claim) {
          this.claim.status = newStatus as any;
          this.addSystemComment(`Statut chang√© √†: ${this.getStatusLabel(newStatus)}`);
        }
        this.updatingStatus = false;
      },
      error: (err) => {
        console.error('Error changing status:', err);
        this.updatingStatus = false;
      }
    });
  }

  addComment(): void {
    if (!this.claim || !this.newComment.trim() || !this.canRespond()) return;

    this.addingComment = true;

    // Utiliser l'endpoint /respond au lieu de /comments
    this.claimService.respondToClaim(this.claim.id, this.newComment).subscribe({
      next: (updatedClaim) => {
        // Ajouter le commentaire √† l'historique local
        this.addSystemComment(this.newComment);
        this.newComment = '';
        this.addingComment = false;
        alert('R√©ponse envoy√©e avec succ√®s!');
      },
      error: (err) => {
        console.error('Error adding comment:', err);
        this.addingComment = false;
        alert('Erreur lors de l\'envoi de la r√©ponse');
      }
    });
  }

  private addSystemComment(message: string): void {
    const systemComment: ClaimComment = {
      id: Date.now(),
      claimId: this.claim!.id,
      author: this.currentUser?.username || 'System',
      comment: message,
      createdAt: new Date()
    };
    this.comments.push(systemComment);
  }

  getStatusLabel(status: string): string {
    return this.statusOptions.find(s => s.value === status)?.label || status;
  }

  getStatusColor(status: string): string {
    return this.statusOptions.find(s => s.value === status)?.color || '#6b7280';
  }

  getPriorityIcon(priority: string): string {
    const icons: any = {
      'LOW': 'üü¢',
      'MEDIUM': 'üü°',
      'HIGH': 'üü†',
      'URGENT': 'üî¥'
    };
    return icons[priority] || '‚ö™';
  }

  getCategoryLabel(category: string): string {
    const labels: any = {
      'INFRASTRUCTURE': 'üèóÔ∏è Infrastructure',
      'SANITATION': 'üßπ Assainissement',
      'LIGHTING': 'üí° √âclairage',
      'SECURITY': 'üõ°Ô∏è S√©curit√©',
      'ADMINISTRATIVE': 'üìã Administratif',
      'OTHER': '‚ùì Autre'
    };
    return labels[category] || category;
  }

  goBack(): void {
    this.router.navigate(['/dashboard/claims']);
  }
}
