  <div *ngIf="!loading && claim">
    <!-- Header avec retour -->
    <div class="page-header">
      <button (click)="goBack()" class="btn-back">‚Üê Retour</button>
      <div>
        <h1>R√©clamation #{{ claim.id }}</h1>
