import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { EmployeeService } from '../../services/employee.service';
import { Employee } from '../../models/employee.model';

@Component({
  selector: 'app-employee-detail',
  templateUrl: './employee-detail.component.html',
  styleUrls: ['./employee-detail.component.css']
})
export class EmployeeDetailComponent implements OnInit {
  employee: Employee | null = null;
  loading = true;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private employeeService: EmployeeService
  ) {}

  ngOnInit(): void {
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.loadEmployee(+id);
    }
  }

  loadEmployee(id: number): void {
    this.employeeService.getEmployee(id).subscribe({
      next: (data) => {
        this.employee = data;
        this.loading = false;
      },
      error: (err) => {
        console.error('Error loading employee:', err);
        this.loading = false;
        this.router.navigate(['/dashboard/hr/employees']);
      }
    });
  }

  goBack(): void {
    this.router.navigate(['/dashboard/hr/employees']);
  }

  editEmployee(): void {
    if (this.employee) {
      this.router.navigate(['/dashboard/hr/employees', this.employee.id, 'edit']);
    }
  }

  deleteEmployee(): void {
    if (!this.employee) return;

    if (confirm(`√ätes-vous s√ªr de vouloir supprimer ${this.employee.firstName} ${this.employee.lastName}?`)) {
      this.employeeService.deleteEmployee(this.employee.id).subscribe({
        next: () => {
          alert('Employ√© supprim√© avec succ√®s');
          this.router.navigate(['/dashboard/hr/employees']);
        },
        error: (err) => {
          console.error('Error deleting employee:', err);
          alert('Erreur lors de la suppression');
        }
      });
    }
  }
}
<div class="page-container">
  <div class="loading" *ngIf="loading">Chargement...</div>

  <div *ngIf="!loading && employee" class="detail-container">
    <div class="detail-header">
      <div>
        <h1>üë§ {{ employee.firstName }} {{ employee.lastName }}</h1>
        <p class="matricule">Matricule: {{ employee.matricule }}</p>
      </div>
      <div class="header-actions">
        <button (click)="goBack()" class="btn btn-secondary">
          ‚Üê Retour
        </button>
        <button (click)="editEmployee()" class="btn btn-primary">
          ‚úèÔ∏è Modifier
        </button>
        <button (click)="deleteEmployee()" class="btn btn-danger">
          üóëÔ∏è Supprimer
        </button>
      </div>
    </div>

    <div class="detail-grid">
      <div class="detail-section">
        <h2>üìã Informations Personnelles</h2>
        <div class="info-grid">
          <div class="info-item">
            <span class="label">Pr√©nom:</span>
            <span class="value">{{ employee.firstName }}</span>
          </div>
          <div class="info-item">
            <span class="label">Nom:</span>
            <span class="value">{{ employee.lastName }}</span>
          </div>
          <div class="info-item">
            <span class="label">Email:</span>
            <span class="value">{{ employee.email }}</span>
          </div>
          <div class="info-item">
            <span class="label">T√©l√©phone:</span>
            <span class="value">{{ employee.phone }}</span>
          </div>
        </div>
      </div>

      <div class="detail-section">
        <h2>üíº Informations Professionnelles</h2>
        <div class="info-grid">
          <div class="info-item">
            <span class="label">D√©partement:</span>
            <span class="value">{{ employee.department }}</span>
          </div>
          <div class="info-item">
            <span class="label">Poste:</span>
            <span class="value">{{ employee.position }}</span>
          </div>
          <div class="info-item">
            <span class="label">Date d'embauche:</span>
            <span class="value">{{ employee.hireDate | date:'dd/MM/yyyy' }}</span>
          </div>
          <div class="info-item">
            <span class="label">Salaire:</span>
            <span class="value">{{ employee.salary | number:'1.2-2' }}‚Ç¨</span>
          </div>
          <div class="info-item">
            <span class="label">Statut:</span>
            <span class="status-badge" [class]="'status-' + employee.status.toLowerCase()">
              {{ employee.status }}
            </span>
          </div>
        </div>
      </div>
    </div>
  </div>

  <div *ngIf="!loading && !employee" class="error-message">
    <h2>‚ùå Employ√© non trouv√©</h2>
    <button (click)="goBack()" class="btn btn-primary">Retour √† la liste</button>
  </div>
</div>
