import { Component, OnInit } from '@angular/core';
import { ReportService } from '../services/report.service';
import { Report, ReportConfig } from '../models/report.model';

@Component({
  selector: 'app-reports',
  templateUrl: './reports.component.html',
  styleUrls: ['./reports.component.css']
})
export class ReportsComponent implements OnInit {
  reports: Report[] = [];
  loading = true;
  generating = false;

  reportConfig: ReportConfig = {
    type: 'HR',
    period: 'MONTHLY',
    format: 'PDF'
  };

  constructor(private reportService: ReportService) {}

  ngOnInit() {
    this.loadReports();
  }

  loadReports() {
    this.reportService.getReports().subscribe({
      next: (data) => {
        this.reports = data;
        this.loading = false;
      },
      error: () => {
        this.loading = false;
      }
    });
  }

  generateReport() {
    this.generating = true;

    // Simuler la g√©n√©ration de rapport (service Reports non disponible)
    setTimeout(() => {
      const newReport = {
        id: Date.now(),
        type: this.reportConfig.type,
        period: this.reportConfig.period,
        format: this.reportConfig.format,
        generatedAt: new Date(),
        generatedBy: 'Admin'
      };

      this.reports.unshift(newReport as any);
      this.generating = false;
      alert('Rapport g√©n√©r√© avec succ√®s (simulation)');
    }, 1000);

    /* Version avec API (quand service Reports sera disponible):
    this.reportService.generateReport(this.reportConfig).subscribe({
      next: (report) => {
        this.generating = false;
        this.loadReports();
        alert('Rapport g√©n√©r√© avec succ√®s');
      },
      error: (error) => {
        this.generating = false;
        console.error('Erreur g√©n√©ration rapport:', error);
        alert('Service Reports non disponible');
      }
    });
    */
  }

  downloadReport(id: number) {
    // Simulation de t√©l√©chargement (service Reports non disponible)
    const report = this.reports.find(r => r.id === id);
    if (!report) {
      alert('Rapport non trouv√©');
      return;
    }

    // Cr√©er un contenu PDF simul√©
    const content = `
Rapport ${report.type}
P√©riode: ${report.period}
Format: ${report.format}
G√©n√©r√© le: ${new Date(report.generatedAt).toLocaleString()}
G√©n√©r√© par: ${report.generatedBy}

Ce rapport a √©t√© g√©n√©r√© avec succ√®s.
(Simulation - Service Reports non disponible)
    `;

    const blob = new Blob([content], { type: 'text/plain' });
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `rapport_${report.type}_${id}.txt`;
    link.click();
    window.URL.revokeObjectURL(url);

    alert('Rapport t√©l√©charg√© avec succ√®s! (simulation)');

    /* Version avec API (quand service Reports sera disponible):
    this.reportService.downloadReport(id).subscribe({
      next: (blob) => {
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = `report_${id}.pdf`;
        link.click();
        window.URL.revokeObjectURL(url);
      },
      error: (error) => {
        alert('Service Reports non disponible');
      }
    });
    */
  }
}
<div class="page-container">
  <div class="page-header">
    <h1>Rapports & Analyses</h1>
  </div>

  <div class="report-generator">
    <h2>G√©n√©rer un nouveau rapport</h2>

    <div class="form-grid">
      <div class="form-group">
        <label>Type de rapport</label>
        <select [(ngModel)]="reportConfig.type" class="form-control">
          <option value="HR">Ressources Humaines</option>
          <option value="BUDGET">Budget</option>
          <option value="CLAIMS">R√©clamations</option>
          <option value="PROJECTS">Projets</option>
          <option value="FINANCIAL">Financier</option>
        </select>
      </div>

      <div class="form-group">
        <label>P√©riode</label>
        <select [(ngModel)]="reportConfig.period" class="form-control">
          <option value="DAILY">Quotidien</option>
          <option value="WEEKLY">Hebdomadaire</option>
          <option value="MONTHLY">Mensuel</option>
          <option value="QUARTERLY">Trimestriel</option>
          <option value="YEARLY">Annuel</option>
          <option value="CUSTOM">Personnalis√©</option>
        </select>
      </div>

      <div class="form-group">
        <label>Format</label>
        <select [(ngModel)]="reportConfig.format" class="form-control">
          <option value="PDF">PDF</option>
          <option value="EXCEL">Excel</option>
          <option value="CSV">CSV</option>
        </select>
      </div>

      <div class="form-group" *ngIf="reportConfig.period === 'CUSTOM'">
        <label>Date de d√©but</label>
        <input type="date" [(ngModel)]="reportConfig.startDate" class="form-control" />
      </div>

      <div class="form-group" *ngIf="reportConfig.period === 'CUSTOM'">
        <label>Date de fin</label>
        <input type="date" [(ngModel)]="reportConfig.endDate" class="form-control" />
      </div>
